package javabasic001;

import java.util.Scanner;

public class CompOpera {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 7;
		x += 3;
		System.out.println(x);
		x -= 3;
		System.out.println(x);
		x *= 3;
		System.out.println(x);
		x /= 3;
		System.out.println(x);
		x %= 3;
		System.out.println(x);
		x = 10;
		System.out.println(x);
		x &= 3;
		System.out.println(x);
		x |= 3;
		System.out.println(x);
		x = 10;
		System.out.println(x);
		x ^= 3;
		System.out.println(x);
		x >>= 3;
		System.out.println(x);
		x <<= 3;
		System.out.println(x);
				
	}
}
