package javabasic001;

import java.util.Scanner;

public class LogiOpera {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 7;
		int y = 4;
		int z = 5;
		System.out.println(x == y);
		System.out.println(x != y);
		System.out.println(x > y);
		System.out.println(x < z);
		System.out.println(x >= z);
		System.out.println(y <= z);
		
				
	}
}
